import React,{useState,useEffect} from "react"
import {Link,useHistory,useRouteMatch,useParams} from "react-router-dom"
import {readDeck,listCards} from "../utils/api/index"

function AddCard() {


  const { deckId } = useParams();
  const history = useHistory()
  const {url} = useRouteMatch()
  const [deck, SetDeck] = useState({});
  const [cards, setCards] = useState([]);
  useEffect(() => {
    async function loadDeck() {
      const getDeck = await readDeck(deckId);
      const getCards = await listCards(deckId);
      SetDeck(getDeck);
      setCards(getCards);
    }
    loadDeck();
  }, []);


    return (
        <div className="container">
            <div className="row">
          <div className="alert alert-dark d-flex flex-row justify-content-start w-75">
              {
                <Link to="/">
                  <p className="mr-2  ml-2 mb-0">
                    <span className="oi oi-home mr-1" />
                    Home{" "}
                  </p>{" "}
                </Link>
              }
              <p className="mr-2 mb-0">/</p>

              {
                <Link to={`/decks/${deckId}`}>
                  <p className="mr-2 ml-2 mb-0">{deck.name} </p>
                </Link>
              }
              <span className="mb-0 mr-2">/</span>
              <p className="mb-0 text-secondary"> Add Card</p>
            </div>
            </div>
            <div className="row">
          <h3>{deck.name}: Add Card</h3>  
          </div>
          <div className="row">
          <div>
        <form>
          <div className="form-group">
            <label for="name">Name</label>

            <input
              class="form-control w-75"
              type="name"
              id="name"
              name="name"
              onChange={handleChange}
              value={formData.name}
              placeholder="Deck Name"
            />

            <label for="description" className="mt-3">
              Description
            </label>
            <textarea
              class="form-control w-75"
              type="description"
              id="description"
              name="description"
              onChange={handleChange}
              value={formData.description}
              placeholder="Brief Description of the Deck"
              rows="5"
            />
          </div>
          <button className="btn btn-secondary mr-2">Cancel</button>
          <button className="btn btn-primary">Submit</button>
        </form>
