import React,{useEffect,useState} from "react"
import {listCards} from "../utils/api/index"

function Deck({name,description,cards}) {
 const [cardNumber,setCardNumber] = useState([])

    useEffect(()=> {
async function cardList() {
  listCards(cards)
  .then((response) => {
      setCardNumber(response)
  } )
}

cardList(cards)
console.log(cardNumber)


}, [])

return (

<div>
       <div className="row mt-2 d-flex justify-content-around align-items-center border "
          style={{width:"60%"}}>
           <div className="col ">
           <h4 className="text-muted"> {name}</h4>
           <p>{description}</p>
           <button className="btn btn-secondary mr-3 mb-1">View</button>
           <button className="btn btn-primary mb-1">Study</button>
           </div>
           <div className="col align-self-start" style={{marginLeft:"200px"}}>
           <p className="text-secondary mb-5" >Number of cards {cardNumber.length} </p>
           <button className="btn btn-danger mt-4 mb-2 ml-2">Delete</button>
           </div>

       </div>
       </div>
)
}

export default Deck
