import React, { useState } from "react";
import { Link } from "react-router-dom";

function CreateDeck() {
  const initialFormState = {
    name: "",
    description: "",
  };
  const [formData, setFormData] = useState({ ...initialFormState });

  const handleChange = ({ target }) => {
    const value = target.value;
    setFormData({
      ...formData,
      [target.name]: value,
    });
  };

  return (
    <div className="container">
      <div className="alert alert-dark d-flex flex-row justify-content-start w-75">
        {
          <Link to="/">
            <p className="mr-2  ml-2 mb-0">
              <span className="oi oi-home mr-1" />
              Home{" "}
            </p>{" "}
          </Link>
        }

        <span className="mb-0 mr-2">/</span>
        <p className="mb-0 text-secondary"> Create Deck</p>
      </div>
      <h1>Create Deck</h1>

      <div>
        <form>
          <div className="form-group">
            <label for="name">Name</label>

            <input
              class="form-control w-50"
              type="name"
              id="name"
              name="name"
              onChange={handleChange}
              value={formData.name}
              placeholder="Deck Name"
            />

          <label for="description">Description</label>
          <textarea
            class="form-control w-50"
            type="description"
            id="description"
            name="description"
            onChange={handleChange}
            value={formData.description}
            placeholder="Brief Description of the Deck"
            rows="5" />
          </div>
        </form>
      </div>
    </div>
  );
}

export default CreateDeck;
