import React,{useState,useEffect} from "react"
import {Link,useHistory,useRouteMatch,useParams} from "react-router-dom"
import {readDeck,listCards} from "../utils/api/index"

function EditCard () {
    const { deckId } = useParams();
    const history = useHistory()
    const {url} = useRouteMatch()
    const [deck, SetDeck] = useState({});
    const [cards, setCards] = useState([]);
    useEffect(() => {
      async function loadDeck() {
        const getDeck = await readDeck(deckId);
        const getCards = await listCards(deckId);
        SetDeck(getDeck);
        setCards(getCards);
      }
      loadDeck();
    }, []);

    const initialFormState = {
      front: "",
      back: "",
    };
    const [formData, setFormData] = useState({ ...initialFormState });

    const handleChange = ({ target }) => {
      const value = target.value;
      setFormData({
        ...formData,
        [target.name]: value,
      });
    };



      return (
          <div className="container">
              <div className="row">
            <div className="alert alert-dark d-flex flex-row justify-content-start w-75">
                {
                  <Link to="/">
                    <p className="mr-2  ml-2 mb-0">
                      <span className="oi oi-home mr-1" />
                      Home{" "}
                    </p>{" "}
                  </Link>
                }
                <p className="mr-2 mb-0">/</p>

                {
                  <Link to={`/decks/${deckId}`}>
                    <p className="mr-2 ml-2 mb-0">{deck.name} </p>
                  </Link>
                }
                <span className="mb-0 mr-2">/</span>
                <p className="mb-0 text-secondary"> Edit Card</p>
                <span className="mb-0 mr-2">/</span>
              </div>
              </div>
              <div className="row ml-0">
            <h3>{deck.name}: Add Card</h3>  
            </div>

            <div>
          <form>
            <div className="form-group">
              <label for="front">Front</label>

              <textarea
                class="form-control w-75"
                type="front"
                id="front"
                name="front"
                onChange={handleChange}
                value={formData.front}
                placeholder="Front of the card"
                rows="3"
              />

              <label for="back" className="mt-3">
                Back
              </label>
              <textarea
                class="form-control w-75"
                type="back"
                id="back"
                name="back"
                onChange={handleChange}
                value={formData.back}
                placeholder="Back of the card"
                rows="3"
              />
            </div>
            <button className="btn btn-secondary mr-2">Done</button>
            <button className="btn btn-primary">Save</button>
          </form>

            </div>












          </div>
      )

}

export default EditCard
