import React,{useState,useEffect} from "react"
import {Link,useHistory,useParams} from "react-router-dom"
import {readDeck,readCard,updateCard} from "../utils/api/index"

function EditCard () {
    const { deckId,cardId } = useParams();
    const history = useHistory()

    const [deck, SetDeck] = useState({});
    const [card, setCard] = useState({});
    const [formData, setFormData] = useState({});
    useEffect(() => {
      async function loadDeck() {
        const getDeck = await readDeck(deckId);
        const getCard = await readCard(cardId)
        SetDeck(getDeck);
        setCard(getCard)

      }
      loadDeck();
    }, []);


    useEffect(() => {
        setFormData({
            front: card.front,
            back: card.back,
            id: cardId,
            deckId: deckId
        })

        }, [card,cardId,deckId])




    const handleChange = ({ target }) => {
      const value = target.value;
      setFormData({
        ...formData,
        [target.name]: value,
      });
    };


    const handleSubmit = (event) => {
        event.preventDefault()
        updateCard(formData)
        .then(() => history.push(`/decks/${deckId}`))

    }

    if(card.front){
      return (
          <div className="container">
              <div className="row">
            <div className="alert alert-dark d-flex flex-row justify-content-start w-75">
                {
                  <Link to="/">
                    <p className="mr-2  ml-2 mb-0">
                      <span className="oi oi-home mr-1" />
                      Home{" "}
                    </p>{" "}
                  </Link>
                }
                <p className="mr-2 mb-0">/</p>

                {
                  <Link to={`/decks/${deckId}`}>
                    <p className="mr-2 ml-2 mb-0">{deck.name} </p>
                  </Link>
                }
                <span className="mb-0 mr-2">/</span>
                <p className="mb-0 text-secondary"> Edit Card {card.id}</p>


              </div>
              </div>
              <div className="row ml-0">
            <h3>{deck.name}: Edit Card</h3>  
            </div>

            <div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label for="front">Front</label>

              <textarea
                class="form-control w-75"
                type="front"
                id="front"
                name="front"
                onChange={handleChange}
                value={formData.front}
                defaultValue={card.front}

                rows="3"
              />

              <label for="back" className="mt-3">
                Back
              </label>
              <textarea
                class="form-control w-75"
                type="back"
                id="back"
                name="back"
                onChange={handleChange}
                value={formData.back}
                defaultValue={card.back}

                rows="3"
              />
            </div>
            <button className="btn btn-secondary mr-2"
            onClick={()=> history.push(`/decks/${deckId}`)}>Cancel</button>
            <button type="submit" className="btn btn-primary">Save</button>
          </form>

            </div>












          </div>
      )
            }
            return "..loading"

}

export default EditCard
