import React,{useEffect,useState} from "react"
import { useHistory,Link,useRouteMatch} from "react-router-dom"
import {deleteDeck} from "../utils/api/index"



function Deck({name,description,cards,deckId}) {
    const history = useHistory()
    const url = useRouteMatch()

    function deleteHandler() {
        if(window.confirm("Delete this Deck?")) {
          deleteDeck(deckId)
        }
    }


return (

<div>
       <div className="row mt-2  justify-content-around align-items-center border mb-2"
          style={{width:"60%"}}>
           <div className="col-4 ">
           <h4 className="text-muted"> {name}</h4>
           <p>{description}</p>
           <button className="btn btn-secondary mr-3 mb-1">View</button>
          <Link to={`${url}/yomamashouse`}> <button className="btn btn-primary mb-1"> Study</button></Link>
           </div>
           <div className="col-2 align-self-start " >

           <p className="text-secondary mb-5" >Number of cards: {cards} </p>

           <button className="btn btn-danger mb-2  " style={{marginTop:"140px"}} onClick={deleteHandler}> Delete</button>
           </div>

       </div>
       </div>
)
}

export default Deck
