import React,{useState,useEffect} from "react"
import {Link,useParams,useHistory} from "react-router-dom"
import {readDeck} from "../utils/api/index"

function EditDeck(){
       const {deckId} = useParams()
       const [deck, SetDeck] = useState({})
       const history = useHistory()

       useEffect(() => {
      async function loadDeck() {
        const getDeck = await readDeck(deckId)
        SetDeck(getDeck)
      }

      loadDeck()
       }, [])




        const initialFormState = {
          name: "",
          description: "",
        };
        const [formData, setFormData] = useState({ ...initialFormState });

        const handleChange = ({ target }) => {
          const value = target.value;
          setFormData({
            ...formData,
            [target.name]: value,
          });
        };



    return (
        <div className="container">
          <div className="alert alert-dark d-flex flex-row justify-content-start w-75">
              {
                <Link to="/">
                  <p className="mr-2  ml-2 mb-0">
                    <span className="oi oi-home mr-1" />
                    Home{" "}
                  </p>{" "}
                </Link>
              }
              <p className="mr-2 mb-0">/</p>

              {
                <Link to={`/decks/${deckId}`}>
                  <p className="mr-2 ml-2 mb-0">{deck.name} </p>
                </Link>
              }
              <span className="mb-0 mr-2">/</span>
              <p className="mb-0 text-secondary"> Study</p>
            </div>
          <h1>Edit Deck</h1>

          <div>
            <form>
              <div className="form-group">
                <label for="name">Name</label>

                <input
                  class="form-control w-75"
                  type="name"
                  id="name"
                  name="name"
                  onChange={handleChange}
                  value={formData.name}
                  placeholder={deck.name}
                />

                <label for="description" className="mt-3">
                  Description
                </label>
                <textarea
                  class="form-control w-75"
                  type="description"
                  id="description"
                  name="description"
                  onChange={handleChange}
                  value={formData.description}
                  placeholder={deck.description}
                  rows="5"
                />
              </div>
              <button className="btn btn-secondary mr-2"
              onClick={()=> history.push(`/decks/${deck.id}`)}>Cancel</button>
              <button className="btn btn-primary">Submit</button>
            </form>
          </div>
        </div>
      );

}

export default EditDeck
